import React, { Component } from 'react';
import styled, { css } from 'styled-components';
import './App.css';

const Input = styled.input`
    margin: 20px 0 20px 0;
    font-size: 20px;
`

const euroCurrency = 4.43;

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentValue: ''
        }
    }

    handleClick = (e) => {
        console.log(e);
        e.preventDefault();
        console.log(e.target.value)

    }

    parseCurrency = (e) => {
        const valueFloat = parseFloat(e.target.value);
        this.setState({
            currentValue: valueFloat * euroCurrency
        })
    }

    render() {
        return (
            <>
                <Input type="text" onChange={this.parseCurrency} />
                <div>{this.state.currentValue}</div>
            </>
        );
    }
}

export default App;
