import React, { Component } from 'react';
import styled, { css } from 'styled-components';
import './App.css';

const Input = styled.input`
    margin: 20px 0 20px 0;
    font-size: 20px;
`

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentEuroValue: 4.43
        }
    }

    handleClick = (e) => {
        e.preventDefault();
        console.log(e.value)
    }

    render() {
        return (
            <>
                <div>Aktualna wartość Euro to {this.state.currentEuroValue}</div>
                <form onSubmit={this.handleClick}>
                    <Input type="text"></Input>
                    <button>Przelicz</button>
                </form>
            </>
        )
    }
}

export default App;
